import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';

const socket = io('http://localhost:5000');

function App() {
  const [badmintonData, setBadmintonData] = useState({
    queue: { queue: [], length: 0, canCreateMatch: false },
    activeMatches: [],
    players: []
  });
  const [playerName, setPlayerName] = useState('');
  const [currentPlayer, setCurrentPlayer] = useState(null);
  const [notification, setNotification] = useState(null);
  const [selectedMatch, setSelectedMatch] = useState(null);
  const [selectedWinners, setSelectedWinners] = useState([]);

  useEffect(() => {
    socket.on('badmintonInitialData', (data) => {
      setBadmintonData(data);
    });

    socket.on('badmintonUpdate', (data) => {
      setBadmintonData(prev => ({
        ...prev,
        queue: data.queue,
        activeMatches: data.activeMatches
      }));
    });

    socket.on('matchCreated', ({ match }) => {
      showNotification(`New match created! Players: ${match.players.map(p => p.name).join(', ')}`);
      if (currentPlayer && match.players.some(p => p.id === currentPlayer.id)) {
        showNotification('You have been selected for a match!', 'success');
      }
    });

    socket.on('matchCompleted', ({ match }) => {
      showNotification(`Match completed! Winners: ${match.result.winners.map(p => p.name).join(', ')}`);
      setSelectedMatch(null);
      setSelectedWinners([]);
    });

    return () => {
      socket.off('badmintonInitialData');
      socket.off('badmintonUpdate');
      socket.off('matchCreated');
      socket.off('matchCompleted');
    };
  }, [currentPlayer]);

  const showNotification = (message, type = 'success') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 5000);
  };

  const joinQueue = async () => {
    if (!userName.trim()) {
      showNotification('Please enter your name', 'error');
      return;
    }

    try {
      const response = await axios.post(`/api/queue/${selectedQueue}/join`, {
        user: { name: userName.trim() }
      });

      if (response.data.success) {
        setCurrentUser(response.data.item);
        showNotification(`Successfully joined the queue! Your position: #${response.data.item.position}`);
        setUserName('');
      } else {
        showNotification(response.data.message, 'error');
      }
    } catch (error) {
      showNotification('Failed to join queue. Please try again.', 'error');
      console.error('Error joining queue:', error);
    }
  };

  const leaveQueue = async (itemId) => {
    try {
      const response = await axios.delete(`/api/queue/${selectedQueue}/item/${itemId}`);

      if (response.data.success) {
        if (currentUser && currentUser.id === itemId) {
          setCurrentUser(null);
        }
        showNotification('Successfully left the queue');
      } else {
        showNotification(response.data.message, 'error');
      }
    } catch (error) {
      showNotification('Failed to leave queue. Please try again.', 'error');
      console.error('Error leaving queue:', error);
    }
  };

  const callNext = async () => {
    try {
      const response = await axios.post(`/api/queue/${selectedQueue}/next`);

      if (response.data.success) {
        showNotification(`Called: ${response.data.item.user.name}`);
      } else {
        showNotification(response.data.message, 'error');
      }
    } catch (error) {
      showNotification('Failed to call next person. Please try again.', 'error');
      console.error('Error calling next:', error);
    }
  };

  const formatTime = (timeString) => {
    const date = new Date(timeString);
    return date.toLocaleTimeString();
  };

  const currentQueue = queues[selectedQueue] || { items: [], length: 0, maxSize: 100 };

  return (
    <div className="container">
      {notification && (
        <div className={`notification ${notification.type === 'error' ? 'error' : ''}`}>
          {notification.message}
        </div>
      )}

      <div className="queue-header">
        <h1>Queue Management System</h1>
        <div className="join-form">
          <input
            type="text"
            placeholder="Enter your name"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && joinQueue()}
            disabled={!!currentUser}
          />
          <button 
            onClick={joinQueue}
            disabled={!!currentUser || !userName.trim()}
          >
            {currentUser ? 'Already in Queue' : 'Join Queue'}
          </button>
        </div>
        {currentUser && (
          <div style={{ marginTop: '10px', padding: '10px', background: '#e3f2fd', borderRadius: '4px' }}>
            You are in position #{currentUser.position} as {currentUser.user.name}
            <button 
              onClick={() => leaveQueue(currentUser.id)}
              style={{ marginLeft: '10px', padding: '5px 10px', background: '#dc3545', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
            >
              Leave Queue
            </button>
          </div>
        )}
      </div>

      <div className="queue-display">
        <h2>Current Queue: {currentQueue.name || 'General Queue'}</h2>

        <div className="queue-stats">
          <div className="stat">
            <div className="stat-value">{currentQueue.length}</div>
            <div className="stat-label">People in Queue</div>
          </div>
          <div className="stat">
            <div className="stat-value">{currentQueue.maxSize - currentQueue.length}</div>
            <div className="stat-label">Available Spots</div>
          </div>
          <div className="stat">
            <div className="stat-value">
              {currentQueue.items.length > 0 ? currentQueue.items[0].position : '-'}
            </div>
            <div className="stat-label">Next in Line</div>
          </div>
        </div>

        {currentQueue.items.length === 0 ? (
          <div style={{ textAlign: 'center', padding: '40px', color: '#666' }}>
            Queue is empty
          </div>
        ) : (
          <ul className="queue-list">
            {currentQueue.items.map((item) => (
              <li 
                key={item.id} 
                className={`queue-item ${currentUser && currentUser.id === item.id ? 'current-user' : ''}`}
              >
                <div className="user-info">
                  <span className="position">#{item.position}</span>
                  <span className="user-name">{item.user.name}</span>
                  <span className="join-time">Joined: {formatTime(item.joinTime)}</span>
                </div>
                {(currentUser && currentUser.id === item.id) && (
                  <button 
                    className="remove-btn"
                    onClick={() => leaveQueue(item.id)}
                  >
                    Leave
                  </button>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="admin-controls">
        <h3>Admin Controls</h3>
        <button 
          className="next-btn"
          onClick={callNext}
          disabled={currentQueue.items.length === 0}
        >
          Call Next Person
        </button>
      </div>
    </div>
  );
}

export default App;
